import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  useNavigate,
} from "react-router-dom";
import "./App.css";
import Database from "./Database";
import Register from "./Register";
import Explanation from "./Explanation";

const LoginPage = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSignIn = async () => {
    if (!username || !password) {
      alert("Please enter both username and password");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();

      if (data.success) {
        localStorage.setItem("token", data.token); // Save token
        alert("Logged in successfully!");
        navigate("/database");
      } else {
        alert("Invalid username or password");
      }
    } catch (err) {
      console.error("Login error:", err);
      alert("Something went wrong. Please try again.");
    }
  };

  return (
    <div className="login-container">
      <h1>Welcome to DOCDOCKET!</h1>
      <p className="intro-text">
        DocDocket is here to serve your documents and dockets. It is a
        revolution in the archiving of legal documents, utilizing blockchain to
        secure documents, while providing AI-driven assistance and granular
        access control to maintain confidentiality.
      </p>
      <button
        className="how-to-button"
        onClick={() => navigate("/explanation")}
      >
        <span className="gear-container">
          <svg
            className="gears"
            xmlns="http://www.w3.org/2000/svg"
            viewBox="0 0 120 120"
          >
            {/* Large Gear */}
            <g className="gear-large">
              <circle cx="60" cy="60" r="40" fill="#2b6cb0" />
              <path
                d="M60 20 L65 35 L80 40 L70 50 L80 60 L65 65 L60 80 L55 65 L40 60 L50 50 L40 40 L55 35 Z"
                fill="#1a202c"
              />
            </g>
            {/* Small Gear */}
            <g className="gear-small" transform="translate(90, 20)">
              <circle cx="0" cy="0" r="15" fill="#2b6cb0" />
              <path
                d="M0 -10 L5 -5 L10 0 L5 5 L0 10 L-5 5 L-10 0 L-5 -5 Z"
                fill="#1a202c"
              />
            </g>
            {/* Tiny Gear */}
            <g className="gear-tiny" transform="translate(30, 90)">
              <circle cx="0" cy="0" r="10" fill="#2b6cb0" />
              <path
                d="M0 -7 L3 -3 L7 0 L3 3 L0 7 L-3 3 L-7 0 L-3 -3 Z"
                fill="#1a202c"
              />
            </g>
          </svg>
        </span>
        How to use DocDocket?
      </button>
      <div className="image-text-container">
        <img
          src="/coolImage.png"
          alt="Cool Illustration"
          className="cool-image"
        />
        <div className="description-text">
          <p>
            <strong>Functionalities:</strong>
            <ul>
              <li>Easy-to-use UI with no need for extra training.</li>
              <li>Offers smart suggestions on client conflict history.</li>
              <li>
                Automate identification of potential COIs (conflicts of
                interest).
              </li>
              <li>Smart keyword and metadata search for fast retrieval.</li>
            </ul>
          </p>
        </div>
      </div>
      <div className="login-form">
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="login-input"
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="login-input"
        />
        <button onClick={handleSignIn} className="login-button">
          Sign In
        </button>
        <p className="footer-text">
          Don't have an account?{" "}
          <a href="#" onClick={() => navigate("/register")}>
            Sign Up
          </a>
        </p>
      </div>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<LoginPage />} />
        <Route path="/register" element={<Register />} />
        <Route path="/database" element={<Database />} />
        <Route path="/explanation" element={<Explanation />} />
      </Routes>
    </Router>
  );
};

export default App;
